package basic;

public class Salariul_anual {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int salariu_per_ora = 20;
		int ore_per_saptamana = 40;
		int saptamani_per_an = 50;
		double rezultat = salariu_per_ora * ore_per_saptamana * saptamani_per_an;
		System.out.println("Inceperea programului:");
		System.out.println("Salariul meu este de : " + rezultat + " Euro pe an");
		System.out.println("Terminarea programului!");
	}

}
